import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import Api from '../Api';
import '../assets/home.css';

const Home = () => {
    const token = localStorage.getItem('token');
    const navigate = useNavigate();

    useEffect(() => {
        if (!token) {
            navigate('/');
        }
    }, [])

    const [boardData, setBoardData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const responses = await Promise.all([
                    Api.get('/v1/board', { params: { token } }),
                ]);

                const [boardResponse] = responses;

                console.log(boardResponse.data.data);
                setBoardData(boardResponse.data.data);
            } catch (error) {
                console.log(error);
                setError('Failed to fetch data.');
            } finally {
                setLoading(false);
            }
        }

        fetchData();
    }, []);

    const handleChange = (e) => {
        setBoardData({
            ...boardData, [e.target.name]: e.target.value
        })
    }

    const handleUpdate = async (e, id) => {
        e.preventDefault();
        try {
            const response = await Api.put(`/v1/board/${id}`, boardData, { params: { token }});
            console.log('update success: ', response.data)
        }
        catch (err) {
            console.log(err);
        }
    }

    if (loading) return <div>Loading...</div>
    if (error) return <div>Error: {error}</div>

    return(
        <>
        <div class="container">
            <div class="board-container">
            
            {boardData && boardData.length > 0 ? (
            boardData.map((board) => (
            <>
                <div class="board-wrapper">
                    <div class="board">
                        <form onSubmit={handleUpdate}>
                            <p>Board Name: </p>
                            <input type="text"
                            placeholder='Are you sure want to delete?' 
                            name='name'
                            value={board.name}
                            onChange={handleChange}/>
                            <input type='submit' value='submit'/>
                        </form>
                            <Link to={`/board/${board.id}`}>Details</Link>
                    </div>
                </div>
            </>
            ))
                ) : (
                    <p>no data</p>
                )}
                <div class="board-wrapper">
                    <div class="board new-board">Create new board...</div>
                </div>
            </div>
        </div>

        </>
    );
}

export default Home;
