import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Api from '../Api';
import '../assets/home.css';

const Home = () => {
    const token = localStorage.getItem('token');
    const navigate = useNavigate();

    useEffect(() => {
        if (!token) {
            navigate('/');
        }
    }, [])

    const [boardData, setBoardData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await Promise.all([
                    Api.get('/v1/board', { params: { token } }),
                ]);

                const boardResponse = response;

                setBoardData(boardResponse);
            } catch (error) {
                console.log(error);
                setError('Failed to fetch data.');
            } finally {
                setLoading(false);
            }
        }

        fetchData();
    }, []);



    return(
        <>
        <div class="container">
            <div class="board-container">
                <div class="board-wrapper">
                    <div class="board">Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod
                        
                    </div>
                    
                </div>

                <div class="board-wrapper"><div class="board new-board">Create new board...</div></div>
            </div>
        </div>
        </>
    );
}

export default Home;
