import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Api from '../Api';
import '../assets/home.css';

const Home = () => {
    const token = localStorage.getItem('token');
    const navigate = useNavigate();

    useEffect(() => {
        if (!token) {
            navigate('/');
        }
    }, [])

    const [boardData, setBoardData] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await Api.get('/v1/')
            } catch (error) {
                console.log(error);
            } finally {
                setLoading(false);
            }
        }

        fetchData();
    }, []);



    return(
        <>
        <div class="container">
            <div class="board-container">
                <div class="board-wrapper"><div class="board">Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod</div></div>
                <div class="board-wrapper"><div class="board">test</div></div>
                <div class="board-wrapper"><div class="board">test</div></div>
                <div class="board-wrapper"><div class="board">test</div></div>
                <div class="board-wrapper"><div class="board">test</div></div>
                <div class="board-wrapper"><div class="board">test</div></div>
                <div class="board-wrapper"><div class="board new-board">Create new board...</div></div>
            </div>
        </div>
        </>
    );
}

export default Home;
